from core.detector import classify_voice
from models import ClassificationEnum
import sys

# Minimal valid MP3 base64 (1 sec silence)
TINY_MP3_BASE64 = ""

def test_classify_voice():
    print("Testing classify_voice logic...")
    try:
        # Test with English
        result = classify_voice(TINY_MP3_BASE64, "English")
        print(f"Result: {result}")
        
        if result["classification"] not in [ClassificationEnum.HUMAN, ClassificationEnum.AI_GENERATED]:
            print("FAILED: Invalid classification enum")
            sys.exit(1)
        
        if not (0.0 <= result["confidenceScore"] <= 1.0):
             print("FAILED: Score out of range")
             sys.exit(1)
             
        if not result["explanation"]:
             print("FAILED: No explanation")
             sys.exit(1)

        print("PASSED")
    except Exception as e:
        print(f"FAILED with exception: {e}")
        sys.exit(1)

if __name__ == "__main__":
    test_classify_voice()
